import React, { useEffect, useState } from 'react';
import { fetchPublications, createPublication, deletePublication } from '../services/api';

function PublicationsPage() {
    const [publications, setPublications] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    useEffect(() => {
        const loadPublications = async () => {
            try {
                const response = await fetchPublications();
                setPublications(response.data);
            } catch (error) {
                alert('Erreur lors du chargement des publications');
            }
        };
        loadPublications();
    }, []);

    const handleAddPublication = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Vous devez être connecté pour ajouter une publication.');
            return;
        }

        try {
            const newPublication = { title, content };
            await createPublication(newPublication, token);
            alert('Publication ajoutée avec succès');
            setTitle('');
            setContent('');
            // Recharger les publications après ajout
            const response = await fetchPublications();
            setPublications(response.data);
        } catch (error) {
            alert('Erreur lors de l\'ajout de la publication');
        }
    };

    const handleDeletePublication = async (id) => {
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Vous devez être connecté pour supprimer une publication.');
            return;
        }

        try {
            await deletePublication(id, token);
            alert('Publication supprimée avec succès');
            // Recharger les publications après suppression
            const response = await fetchPublications();
            setPublications(response.data);
        } catch (error) {
            alert('Erreur lors de la suppression de la publication');
        }
    };

    return (
        <div>
            <h1>Publications</h1>
            <form onSubmit={handleAddPublication}>
                <input
                    type="text"
                    placeholder="Titre"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <textarea
                    placeholder="Contenu"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                />
                <button type="submit">Ajouter une publication</button>
            </form>
            <ul>
                {publications.map((pub) => (
                    <li key={pub._id}>
                        <h2>{pub.title}</h2>
                        <p>{pub.content}</p>
                        <button onClick={() => handleDeletePublication(pub._id)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default PublicationsPage;
